mapper.select:
SELECT user.user_seq AS user_seq, user.id AS id, user.name AS name, count(1) AS game_count, count(if(score.order=1, score.order, null)) AS first_count, count(if(score.order=2, score.order, null)) AS second_count, count(if(score.order=3, score.order, null)) AS third_count, count(if(score.order=4, score.order, null)) AS fourth_count, count(if(score.yakuman_kind!=null, score.yakuman_kind, null)) AS yakuman_count, count(if(score.hakoten_yn='Y', score.hakoten_yn, null)) AS hakoten_count, sum(score.score) AS total_score, user.reg_dt AS reg_dt, user.mod_dt AS mod_dt FROM user LEFT JOIN score ON user.user_seq = score.user_seq
WHERE score.season = #{season}
GROUP BY user.user_seq

mapper.insert:
INSERT INTO
  user (
    id, name
  )
VALUES (
  #{id}, #{name}
)
